import "./App.css";
import React, { useContext, useEffect} from "react";
import Home from "./Pages/Home";
import BlogPage from "./Pages/BlogPage";
import TagPage from "./Pages/TagPage";
import Categorypage from "./Pages/Categorypage";
import { AppContext } from "./context/AppContext";
import { useLocation, useSearchParams } from "react-router-dom";
import { Route } from "react-router-dom";
import { Routes } from "react-router-dom";



const App = () => {


  const {fetchBlogPosts} = useContext(AppContext);
  const [searchParams , setSearchParams] = useSearchParams();
  const location = useLocation();



   useEffect(()=>{
    //page ko search kro jiski key page h , or agar koi page nhi h to by default 1 hga
    const page = searchParams.get("page") ?? 1;
    const tag = location.pathname.split("/").at(-1).replace("_"," ");
    const category = location.pathname.split("/").at(-1).replace("_"," ");

    if(location.pathname.includes("tags")) {
      //iska mtlb tag show kardo
      //jo last wale slash ke bad ki value h use nikal kar le aao

      fetchBlogPosts(Number(page),tag);
    }
    else if(location.pathname.includes("category")){

      fetchBlogPosts(Number(page),null,category)
    }
    else{
      fetchBlogPosts(Number(page));
    }

   },[location.pathname , location.search]);


  return (
         <Routes>

          <Route path='/' element={<Home/>} />
          <Route path='/blog/:blogId' element={<BlogPage/>} />
          <Route path='/tags/:tag' element={<TagPage/>} />
          <Route path='/categories/:category' element={<Categorypage/>} />

         </Routes>
  );
}
export default App;
