import React,{useState ,useEffect} from 'react';
import './Header.css';
const Header = () => {
    const[theme , setTheme] = useState('light');

    
  useEffect(()=>{
    const savedTheme = localStorage.getItem('theme');
    if(savedTheme){
      setTheme(savedTheme);
    }
  },[])

  useEffect(()=>{
    document.body.className = theme + '-mode';
    localStorage.setItem('theme',theme);
  },[theme])

  const toggleTheme =() =>{
    setTheme((prevTheme)=>(prevTheme === 'light' ? 'dark' : 'light')) ;
  };
    return(
        

       <div className= {`${theme ==='light' ? "bg-white text-black  " : "bg-black text-white border-[#e7e5e4]"}
       ' border-5  border-x-0 w-full  fixed top-0  header flex justify-center items-center`}>

<div className="flex bg-[#262626] p-1 my-4  rounded-full max-w-[50px] mx-auto justify-between">

<button 
className={`${
    theme === "light"
      ? "bg-[#050505] text-[#e5e5e5]"
      : "bg-transparent text-[#d4d4d4] "
  } py-2 px-5 rounded-full transition-all`}
onClick={()=> setTheme("light")}>light</button>

<button className={`${
    theme === "dark"
      ? "bg-[white] text-[#e5e5e5]"
      : "bg-transparent text-[#d4d4d4] "
  } py-2 px-5 rounded-full transition-all`}
onClick={()=> setTheme("dark")}>Dark</button>
</div>
           
                     <h1 className='text-2xl font-bold text-center'>TORNED EDU HELP BLOGS</h1>
           
            
           </div>
        
    )
}
export default Header;
