import React, { useContext } from 'react';

const ToggleButton =() =>{
    const {toggleTheme , theme } = useContext(ThemeContext);

    return <button onClick={{toggleTheme}}>
                {theme === 'light' ? 'Dark-mode' : 'light-mode'};
            </button>
}
export default ToggleButton;
