import React, { useContext } from 'react';
import { AppContext } from './AppContext'; // Import your context here
import Spinner from './Spinner'; // Assuming Spinner component is defined
import Card from './Card'; // Assuming Card component is defined

const Blogs = () => {
    const { posts, loading } = useContext(AppContext); // Assuming AppContext provides posts and loading state

    return (
        <div>
            {loading ? (
                <Spinner />
            ) : (
                posts.length === 0 ? (
                    <p>No blogs</p>
                ) : (
                    posts.map((post, index) => (
                        <Card key={index} post={post} />
                    ))
                )
            )}
        </div>
    );
};

export default Blogs;
